n        // @param _value The amount of token to be transferred.\n        // @return Whether the transfer was successful or not.\n        virtual bool transfer(Address _to, uint64_t _value) = 0;\n\n        // @notice send `_value` token to `_to` from `_from` on the condition it is approved by `_from`\n        // @param _from The address of the sender.\n        // @param _to The address of the recepient.\n        // @param _value The amount of token to be transferred.\n        // @return Whether the transfer was successful or not.\n        virtual bool transferFrom(Address _from, Address _to, uint64_t _value) = 0;\n\n        // @notice `msg.sender` approves `_spender` to spend `_value` tokens\n        // @param _spender The address of the account able to transfer the tokens\n        // @param _value The amount of tokens to be approved for transfer\n        // @return Whether thee approval was successful or not.\n        virtual bool approve(Address _spender, uint64_t _value) = 0;\n\n        // @param _owner The address of the account owning tokens\n        // @param _spender The address of the account able to transfer the tokens\n        // @return Amount of remaining tokens allowed to spent.\n        virtual uint64_t allowance(Address _owner, Address _spender) = 0;\n};\n\n// You should inherit from StandardTOken or, for a token like you would want\n// to deploy in something like MIst, see HumanStandardToken.cpp.\n// (This implements ONLY the standard functions and NOTHING else.\n// If you deploy this, you won\'t have anthing useful.)\nclass StandardToken: public Token\n{\n    protected:\n        platon::StorageType<"balances"_n, std::map<Address, uint64_t>> balances;\n        platon::StorageType<"allowed"_n, std::map<Address, std::map<Address, uint64_t>>> allowed;\n\n    public:\n        CONST uint64_t balanceOf(Address _owner) {\n            return balances.self()[_owner];\n        }\n\n        ACTION bool transfer(Address _to, uint64_t _value){\n            // Default assumes totalSupply can\'t be over max(2^64 - 1)\n            // If your token leaves out totalSupply and can issue more tokens as time goes on,\n            // you need to check if it doesn\'t wrap.\n            // Replace the if with this on instead.\n            Address sender = platon_caller();\n            if (balances.self()[sender] >= _value && _value > 0) {\n                balances.self()[sender] -= _value;\n                balances.self()[_to] += _value;\n                PLATON_EMIT_EVENT2(Transfer, sender, _to, _value);\n                return true;\n            } else {\n                return false;\n            }\n        }\n\n        ACTION bool transferFrom(Address _from, Address _to, uint64_t _value) {\n            // same as above. Replace this line with the following if you want to protect against\n            // wrapping uints.\n            Address sender = platon_caller();\n            if(balances.self()[_from] >= _value\n                && allowed.self()[_from][sender] >= _value && _value > 0){\n                balances.self()[_to] += _value;\n                balances.self()[_from] -= _value;\n                PLATON_EMIT_EVENT2(Transfer, _from, _to, _value);\n                return true;\n            } else {\n                return false;\n            }\n        }\n\n        ACTION bool approve(Address _spender, uint64_t _value){\n            Address sender = platon_caller();\n            allowed.self()[sender][_spender] = _value;\n            PLATON_EMIT_EVENT2(Approval, sender, _spender, _value);\n            return true;\n        }\n\n        CONST uint64_t allowance(Address _owner, Address _spender){\n            return allowed.self()[_owner][_spender];\n        }\n};\n\n\nCONTRACT LATToken: public platon::Contract, public StandardToken\n{\n\n    public:\n        platon::StorageType<"name"_n, std::string> name;        // fancy name: eg Alaya Token\n        platon::StorageType<"decimals"_n, uint8_t> decimals;    // HOw many decimals to show.\n        platon::StorageType<"symbol"_n, std::string> symbol;    // An identifier: eg LTT\n        platon::StorageType<"version"_n, std::string> version;  // 0.1 standard. Just an arbitrary versioning scheme.\n\n    public:\n        ACTION void init(uint64_t _initialAmount, const std::string& _tokenName,\n            uint8_t _decimalUnits, const std::string& _tokenSymbol)\n        {\n            Address sender = platon_caller();\n            balances.self()[sender] = _initialAmount;       // Give the creator all initial tokens.\n            totalSupply.self() = _initialAmount;            // Update total supply.\n            name.self() = _tokenName;                       // Set the name for display purposes\n            decimals.self() = _decimalUnits;                // Amount of decimals for display purposes\n            symbol.self() = _tokenSymbol;                   // Set the symbol for display purposes.\n        }\n\n        CONST std::string getName(){\n            return name.self();\n        }\n\n        CONST uint8_t getDecimals(){\n            return decimals.self();\n        }\n\n        CONST std::string getSymbol(){\n            return symbol.self();\n        }\n\n        CONST uint64_t getTotalSupply(){\n            return totalSupply.self();\n        }\n\n        // Approves and then calls the receiving contract.\n        ACTION bool approveAndCall(Address _spender, uint64_t _value, const bytes& _extraData) {\n            Address sender = platon_caller();\n            allowed.self()[sender][_spender] = _value;\n            PLATON_EMIT_EVENT2(Approval, sender, _spender, _value);\n            // call the receiveApproval function on the contract you want to be notified. This\n            // crafts the function signature manually so one doesn\'t have to include a contract\n            // in here just for this.\n            // define: receiveApproval(Address _from, uint64_t _value, Address _tokenContract, bytes& _extraDaa)\n            // it is assumed that when does this that the call should succeed.\n            return true;\n        }\n};\n\nPLATON_DISPATCH(LATToken,(init)(balanceOf)(transfer)(transferFrom)(approve)(allowance)\n(getName)(getDecimals)(getSymbol)(getTotalSupply)(approveAndCall))\n')),(0,r.kt)("p",null,"ByteCode"),(0,r.kt)("p",null,(0,r.kt)("a",{parentName:"p",href:"https://github.com/PlatONnetwork/alaya-devdocs/blob/master/zh-cn/Development/%E6%99%BA%E8%83%BD%E5%90%88%E7%BA%A6%E5%BC%80%E5%8F%91.assets/Token_wasm.bin"},"Click to view bincode")),(0,r.kt)("p",null,"ByteSize: ",(0,r.kt)("inlineCode",{parentName:"p"},"36764 byte => 35.9 kb")),(0,r.kt)("hr",null),(0,r.kt)("h6",{id:"costs-1"},"Costs"),(0,r.kt)("p",null,"Alaya-EVM"),(0,r.kt)("ul",null,(0,r.kt)("li",{parentName:"ul"},"GasUsed: 552823"),(0,r.kt)("li",{parentName:"ul"},"GasPrice: 5,000,000,000 (5 ",(0,r.kt)("inlineCode",{parentName:"li"},"gVON"),")"),(0,r.kt)("li",{parentName:"ul"},"Costs: 2764115 ",(0,r.kt)("inlineCode",{parentName:"li"},"gVON"),"\uff080.002764115 ",(0,r.kt)("inlineCode",{parentName:"li"},"ATP"),"\uff09")),(0,r.kt)("p",null,"Ethereum"),(0,r.kt)("ul",null,(0,r.kt)("li",{parentName:"ul"},"GasUsed: 1282171"),(0,r.kt)("li",{parentName:"ul"},"GasPrice: 5,000,000,000 (5 ",(0,r.kt)("inlineCode",{parentName:"li"},"Gwei"),")"),(0,r.kt)("li",{parentName:"ul"},"Costs: 6410855 ",(0,r.kt)("inlineCode",{parentName:"li"},"Gwei"),"\uff080.006410855 ",(0,r.kt)("inlineCode",{parentName:"li"},"ETH"),"\uff09")),(0,r.kt)("p",null,"Alaya-WASM"),(0,r.kt)("ul",null,(0,r.kt)("li",{parentName:"ul"},"GasUsed: 486274"),(0,r.kt)("li",{parentName:"ul"},"GasPrice: 5,000,000,000 (5 ",(0,r.kt)("inlineCode",{parentName:"li"},"gVON"),")"),(0,r.kt)("li",{parentName:"ul"},"Costs: 2431370 ",(0,r.kt)("inlineCode",{parentName:"li"},"gVON"),"\uff080.00243137 ",(0,r.kt)("inlineCode",{parentName:"li"},"ATP"),"\uff09")),(0,r.kt)("hr",null),(0,r.kt)("h2",{id:"best-practice"},"Best Practice"),(0,r.kt)("h3",{id:"introduction-1"},"Introduction"),(0,r.kt)("p",null,"This guide introduces users to some key points that need to be paid attention to\nin the development of smart contracts, mainly in the practice of actual\ndevelopment. Users can use this guide to quickly understand how to set a\nreasonable fee for a transaction, how to avoid losing the fee due to transaction\nfailure, and how to encode a more standardized smart contract."),(0,r.kt)("h3",{id:"reasonable-cost-setting"},"Reasonable Cost Setting"),(0,r.kt)("p",null,"When you need to deploy a contract on the main network of Alaya, you need to set a reasonable fee limit. The fee limit refers to the upper limit of the energy consumption cost of smart contract deployment/execution in Alaya. This restriction is mainly accomplished through Gas. Gas is the fuel value of the Alaya network world, which determines the normal operation of the Alaya network ecosystem. Gas is usually used to measure how much \u201cwork\u201d is required to perform certain actions, and these workloads are the amount of fees that need to be paid to the Alaya network in order to perform the action. In a simple understanding, Gas is the commission for network miners, and is paid by ATP. Any transaction, contract execution, and data storage on the network need to use Gas."),(0,r.kt)("p",null,"Alaya is similar to Ethereum\u2019s blockchain system. It uses ",(0,r.kt)("inlineCode",{parentName:"p"},"ATP")," for payment and\nmaintenance networks. One ",(0,r.kt)("inlineCode",{parentName:"p"},"ATP")," is divided into: ",(0,r.kt)("inlineCode",{parentName:"p"},"mATP/uATP/gVON/mVON/kVON/VON"),", and ",(0,r.kt)("inlineCode",{parentName:"p"},"VON")," is the smallest unit."),(0,r.kt)("p",null,"Gas consists of two parts: GasLimit and GasPrice. GasLimit is the maximum Gas consumption (minimum 21,000) that a user is willing to pay to perform an operation or confirm a transaction. GasPrice is the unit price of each Gas."),(0,r.kt)("p",null,"When a user sends a transaction, GasLimit and GasPrice are set. GasLimit ","*"," GasPrice is the user\u2019s transaction cost, and the cost is rewarded to the miner as a commission."),(0,r.kt)("p",null,"The higher the GasPrice of the transaction, the higher the execution priority of the transaction and the greater the transaction cost. After each transaction is completed, the remaining unused Gas will be returned to the sender\u2019s address account. It is important to note that if the execution of the transaction fails because the GasLimit is set too low, the Gas will not be returned to the user\u2019s address at this time, and the user still needs to pay the energy cost for the failed transaction. Therefore, regardless of whether the transaction is executed successfully, the transaction sender needs to pay a certain calculation fee to the miner."),(0,r.kt)("p",null,"In the Alaya network, the maximum gas limit is ",(0,r.kt)("inlineCode",{parentName:"p"},"100,800,000")," and the minimum is 22,000. Too low or too high will cause transaction failure. When deploying large contracts or calling complex functions in contracts, you can increase the gas limit, for example: 1,000,000. If it is a normal transfer, set it to the lowest value. The specific value needs to be estimated according to the size and complexity of the contract. Before the contract is released, the interface platon_estimateGas can be called for approximate estimation to avoid failure due to insufficient Gas."),(0,r.kt)("p",null,(0,r.kt)("strong",{parentName:"p"},"ATP Unit Conversion")),(0,r.kt)("table",null,(0,r.kt)("thead",{parentName:"table"},(0,r.kt)("tr",{parentName:"thead"},(0,r.kt)("th",{parentName:"tr",align:null},"Unit"),(0,r.kt)("th",{parentName:"tr",align:null},"VON Value"),(0,r.kt)("th",{parentName:"tr",align:null},"VON"))),(0,r.kt)("tbody",{parentName:"table"},(0,r.kt)("tr",{parentName:"tbody"},(0,r.kt)("td",{parentName:"tr",align:null},"VON"),(0,r.kt)("td",{parentName:"tr",align:null},"1"),(0,r.kt)("td",{parentName:"tr",align:null},"1")),(0,r.kt)("tr",{parentName:"tbody"}